export class FileUploadComponent {
    /**
     * Constructor.
     *
     * @param fileUploadService the injected file upload service
     * @param fb the injected form builder
     */
    constructor(fileUploadService, fb) {
        this.fileUploadService = fileUploadService;
        this.fb = fb;
        this.fileRemoved = new EventEmitter();
        this.fileAdded = new EventEmitter();
        this.fileModified = new EventEmitter();
        this.fileUploadGroup = this.fb.group({
            fileUpload: [null, []],
        });
        this.isSaving = false;
        this.disabled = false;
        this._fileId = -1;
        this._originalId = -1;
        this._fileRemoved = false;
        this._fileSelected = false;
    }
